targets.expr:
sum by (cluster, namespace, pod, container, node) (
  kube_pod_container_status_last_terminated_reason{cluster=~"$cluster", namespace=~"$namespace", pod=~"$pod", container=~"$container", reason="OOMKilled"}
  * on (cluster, namespace, pod) group_left(node)
    kube_pod_info{cluster=~"$cluster", namespace=~"$namespace", node=~"$node", pod=~"$pod"}
)
